import {
  users, type User, type InsertUser,
  messages, type Message, type InsertMessage,
  comments, type Comment, type InsertComment,
  faculty, type Faculty,
  counselors, type Counselor,
  counselorSessions, type CounselorSession, type InsertCounselorSession
} from "@shared/schema";

export interface IStorage {
  
  getUser(id: number): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, data: Partial<User>): Promise<User | undefined>;
  
  createMessage(message: Omit<InsertMessage, "userId"> & { userId: number }): Promise<Message>;
  getMessages(): Promise<Message[]>;
  getMessagesByRecipientType(recipientType: string): Promise<Message[]>;
  getMessageById(id: number): Promise<Message | undefined>;
  updateMessage(id: number, data: Partial<Message>): Promise<Message | undefined>;
  
  createComment(comment: Omit<InsertComment, "userId"> & { userId: number }): Promise<Comment>;
  getCommentsByMessageId(messageId: number): Promise<Comment[]>;
  
  getFaculty(): Promise<Faculty[]>;
  getFacultyById(id: number): Promise<Faculty | undefined>;
  
  getCounselors(): Promise<Counselor[]>;
  getCounselorById(id: number): Promise<Counselor | undefined>;
  
  createCounselorSession(session: InsertCounselorSession): Promise<CounselorSession>;
  getCounselorSessionsByUserId(userId: number): Promise<CounselorSession[]>;
  getCounselorSessionsByCounselorId(counselorId: number): Promise<CounselorSession[]>;
  bookCounselorSession(id: number, userId: number): Promise<CounselorSession | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private messages: Map<number, Message>;
  private comments: Map<number, Comment>;
  private faculty: Map<number, Faculty>;
  private counselors: Map<number, Counselor>;
  private counselorSessions: Map<number, CounselorSession>;
  private userId: number;
  private messageId: number;
  private commentId: number;
  private facultyId: number;
  private counselorId: number;
  private sessionId: number;

  constructor() {
    this.users = new Map();
    this.messages = new Map();
    this.comments = new Map();
    this.faculty = new Map();
    this.counselors = new Map();
    this.counselorSessions = new Map();
    this.userId = 1;
    this.messageId = 1;
    this.commentId = 1;
    this.facultyId = 1;
    this.counselorId = 1;
    this.sessionId = 1;
    
    this.initializeData();
  }
  
  private initializeData() {
    
    this.faculty.set(this.facultyId++, {
      id: 1,
      name: "Dr. Bholanath Roy",
      department: "Computer Science",
      email: " bholanathroy@manit.ac.in",
      officeHours: "Mon, Wed 2-4 PM",
      imageUrl: "data:image/jpeg;base64,"
    });
    
    this.faculty.set(this.facultyId++, {
      id: 2,
      name: "Dr. Meenu Chawla",
      department: "Computer Science",
      email: "chawlam@manit.ac.in",
      officeHours: "Tue, Thu 10-12 PM",
      imageUrl: "data:image/jpeg;base64,"
    });
    
    this.faculty.set(this.facultyId++, {
      id: 3,
      name: "Dr. Deepak Singh Tomar",
      department: "Computer Science",
      email: "deepaktomar@manit.ac.in",
      officeHours: "Tue, Fri 8-10 PM",
      imageUrl: "data:image/jpeg;base64,"
    });
    
    this.counselors.set(this.counselorId++, {
      id: 1,
      name: "Ms. zeba",
      title: "Mental Health Counselor",
      email: "zebapsychologist18@gmail.com",
      specialties: "Anxiety, Depression, Academic Stress",
      imageUrl: "data:image/jpeg;base64,"
    });
    
    this.counselors.set(this.counselorId++, {
      id: 2,
      name: "Dr. Kamlesh Tiwari",
      title: "Clinical Psychologist",
      email: "drkamlesh1960@gmail.com",
      specialties: "Trauma, Relationship Issues, Self-esteem",
      imageUrl: "data:image/jpeg;base64,"
    });
  }
  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    for (const user of this.users.values()) {
      if (user.email === email) {
        return user;
      }
    }
    return undefined;
  }

  async createUser(user: InsertUser): Promise<User> {
    const id = this.userId++;
    const newUser: User = { 
      ...user,
      id, 
      email: user.email,
      name: user.name || null,
      profession: user.profession || null,
      whatsappNumber: user.whatsappNumber || null,
      password: user.password,
      profileComplete: user.profileComplete !== undefined ? user.profileComplete : false,
      viewedIntro: user.viewedIntro !== undefined ? user.viewedIntro : false,
      createdAt: new Date() 
    };
    this.users.set(id, newUser);
    return newUser;
  }

  async updateUser(id: number, data: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;
    
    const updatedUser = { ...user, ...data };
    this.users.set(id, updatedUser);
    return updatedUser;
  }
  async createMessage(message: Omit<InsertMessage, "userId"> & { userId: number }): Promise<Message> {
    const id = this.messageId++;
    const newMessage: Message = { 
      ...message, 
      id, 
      content: message.content,
      isAnonymous: message.isAnonymous || false,
      recipientType: message.recipientType,
      isFlagged: false, 
      flaggedContent: null,
      isCrisis: false,
      crisisType: null,
      notifiedCounselor: false,
      createdAt: new Date() 
    };
    this.messages.set(id, newMessage);
    return newMessage;
  }

  async getMessages(): Promise<Message[]> {
    return Array.from(this.messages.values());
  }

  async getMessagesByRecipientType(recipientType: string): Promise<Message[]> {
    return Array.from(this.messages.values()).filter(
      message => message.recipientType === recipientType
    );
  }

  async getMessageById(id: number): Promise<Message | undefined> {
    return this.messages.get(id);
  }

  async updateMessage(id: number, data: Partial<Message>): Promise<Message | undefined> {
    const message = this.messages.get(id);
    if (!message) return undefined;
    
    const updatedMessage = { ...message, ...data };
    this.messages.set(id, updatedMessage);
    return updatedMessage;
  }
  async createComment(comment: Omit<InsertComment, "userId"> & { userId: number }): Promise<Comment> {
    const id = this.commentId++;
    const newComment: Comment = { 
      ...comment, 
      id, 
      content: comment.content,
      messageId: comment.messageId,
      userId: comment.userId,
      createdAt: new Date() 
    };
    this.comments.set(id, newComment);
    return newComment;
  }

  async getCommentsByMessageId(messageId: number): Promise<Comment[]> {
    return Array.from(this.comments.values()).filter(
      comment => comment.messageId === messageId
    );
  }

  async getFaculty(): Promise<Faculty[]> {
    return Array.from(this.faculty.values());
  }

  async getFacultyById(id: number): Promise<Faculty | undefined> {
    return this.faculty.get(id);
  }


  async getCounselors(): Promise<Counselor[]> {
    return Array.from(this.counselors.values());
  }

  async getCounselorById(id: number): Promise<Counselor | undefined> {
    return this.counselors.get(id);
  }

  async createCounselorSession(session: InsertCounselorSession): Promise<CounselorSession> {
    const id = this.sessionId++;
    const newSession: CounselorSession = { ...session, id, isBooked: false };
    this.counselorSessions.set(id, newSession);
    return newSession;
  }

  async getCounselorSessionsByUserId(userId: number): Promise<CounselorSession[]> {
    return Array.from(this.counselorSessions.values()).filter(
      session => session.userId === userId
    );
  }

  async getCounselorSessionsByCounselorId(counselorId: number): Promise<CounselorSession[]> {
    return Array.from(this.counselorSessions.values()).filter(
      session => session.counselorId === counselorId
    );
  }

  async bookCounselorSession(id: number, userId: number): Promise<CounselorSession | undefined> {
    const session = this.counselorSessions.get(id);
    if (!session || session.isBooked) return undefined;
    
    const bookedSession = { ...session, userId, isBooked: true };
    this.counselorSessions.set(id, bookedSession);
    return bookedSession;
  }
}

export const storage = new MemStorage();

